package seckill\.service\.impl\;

import org\.slf4j\.Logger\;
import org\.slf4j\.LoggerFactory\;
import org\.springframework\.util\.DigestUtils\;
import seckill\.dao\.SeckillDao\;
import seckill\.dao\.SuccessKilledDao\;
import seckill\.dto\.Exposer\;
import seckill\.entity\.Seckill\;
import seckill\.entity\.SuccessKilled\;
import seckill\.exception\.RepeatKillException\;
import seckill\.exception\.SeckillCloseException\;
import seckill\.exception\.SeckillException\;
import seckill\.service\.SeckillService\;

import java\.util\.Date\;
import java\.util\.List\;

public class SeckillServiceImpl implements SeckillService \{

 private Logger logger \= LoggerFactory\.getLogger\(this\.getClass\(\)\)\;

 private SeckillDao seckillDao\;

 private SuccessKilledDao successKilledDao\;

 //混淆\，让用户猜不到这是什么\,用于混淆md5
 private final String slat\="df\#\@\%\@\￥\%salDFGAGJALKkf47546jalkffsdasa"\;

 public List\<Seckill\> getSeckillList\(\) \{
 return seckillDao\.queryAll\(0\, 4\)\;
 \}

 public Seckill getById\(long seckillId\) \{
 return seckillDao\.queryById\(seckillId\)\;
 \}

 /\*
 \* 秒杀开启时输出接口地址\，
 \* 否则输出系统时间和秒杀时间
 \*
 \*/
 public Exposer exportSeckillUrl\(long seckillId\) \{
 Seckill seckill \= seckillDao\.queryById\(seckillId\)\;

 if\(seckill \=\= null\)\{
 return new Exposer\(false\, seckillId\)\;
 \}
 Date startTime \= seckill\.getStartTime\(\)\;
 Date endTime \= seckill\.getEndTime\(\)\;
 //当前时间
 Date nowTime \= new Date\(\)\;

 if\(nowTime\.getTime\(\) \< startTime\.getTime\(\)
 \|\| nowTime\.getTime\(\) \> endTime\.getTime\(\)\)\{
 return new Exposer\(false\,seckillId\,nowTime\.getTime\(\)\,startTime\.getTime\(\)\,endTime\.getTime\(\)\)\;
 \}
 //转化特定字符串的过程\，不可逆
 String md5 \= getMD5\(seckillId\)\; //TODO
 return new Exposer\(true\,md5\,seckillId\)\;
 \}

 //生成md5
 private String getMD5\(long seckillId\)\{
 String base \= seckillId \+ "/" \+ slat\;
 String md5 \= DigestUtils\.md5DigestAsHex\(base\.getBytes\(\)\)\;
 return md5\;
 \}

 public SecurityException executeSeckill\(long seckillId\, long userPhone\, String md5\)
 throws SeckillException\, RepeatKillException\, SeckillCloseException \{

 //通过md5判断
 if\(md5 \=\= null \|\| md5\.equals\(getMD5\(seckillId\)\)\)\{
 throw new SeckillException\("seckill data rewrite"\)\;
 \}

 //执行秒杀逻辑\：减库存\+记录购买记录
 Date nowTime \= new Date\(\)\;
 try \{
 int updateCount \= seckillDao\.reduceNumber\(seckillId\, nowTime\)\;
 if\(updateCount \<\= 0\)\{
 //没有更新到记录\,秒杀结束
 throw new SeckillCloseException\("seckill is closed"\)
 \} else \{
 //减库存成功\，记录购买行为
 int insertCount \= successKilledDao\.insertSuccessKilled\(seckillId\, userPhone\)\;
 //验证唯一\：seckillId\,userPhone
 if \(insertCount \<\= 0\) \{
 //重复秒杀
 throw new RepeatKillException\("seckill repeated"\)\;
 \} else \{
 //秒杀成功
 SuccessKilled successKilled \= successKilledDao\.queryByIdWithSeckill\(seckillId\, userPhone\)\;
 return new SeckillException\(seckillId\, 1\, "秒杀成功"\, successKilled\)\;
 \}
 \}
 \} catch \(Exception e\)\{
 logger\.error\(\)\;
 throw new SeckillException\("seckill inner error" \+ executeSeckill\(\)\.getMessage\(\)\)\;
 \}
 \}
 return null\;
 \}
\}

package seckill\.web\;

import org\.slf4j\.Logger\;
import org\.slf4j\.LoggerFactory\;
import org\.springframework\.beans\.factory\.annotation\.Autowired\;
import org\.springframework\.stereotype\.Controller\;
import org\.springframework\.ui\.Model\;
import org\.springframework\.web\.bind\.annotation\.\*\;
import seckill\.SeckillStateEnum\.SeckillStateEnum\;
import seckill\.dto\.Exposer\;
import seckill\.dto\.SeckillExecution\;
import seckill\.dto\.SeckillResult\;
import seckill\.entity\.Seckill\;
import seckill\.exception\.RepeatKillException\;
import seckill\.exception\.SeckillCloseException\;
import seckill\.exception\.SeckillException\;
import seckill\.service\.SeckillService\;

import javax\.annotation\.Resource\;
import java\.util\.List\;

\@Controller
\@RequestMapping\("/seckill"\) //url\:/模块/资源/\｛id\｝/细分
public class SeckillController \{

 private final Logger logger \= LoggerFactory\.getLogger\(this\.getClass\(\)\)\;

 \@Autowired
 private SeckillService seckillService\;

 \@RequestMapping\(name \= "/list"\,method \= RequestMethod\.GET\)
 public String list\(Model model\) \{
 //获取列表页
 List\<Seckill\> list \= seckillService\.getSeckillList\(\)\;
 model\.addAttribute\("list"\, list\)\;
 //list\.JSP \+ model \= ModelAndView
 return "list"\;
 \}

 \@RequestMapping\(value \= "/\{seckillId\}/detail"\,method \= RequestMethod\.GET\)
 public String detail\(\@PathVariable\("seckillId"\) Long seckillId\, Model model\) \{
 if \(seckillId \=\= null\) \{
 return "redirect\:/seckill/list"\;
 \}
 Seckill seckill \= seckillService\.getById\(seckillId\)\;
 if \(seckill \=\= null\) \{
 return "forward\:/seckill/list"\;
 \}
 model\.addAttribute\("seckill"\,seckill\)\;
 return "detail"\;
 \}

 //ajax json
 \@RequestMapping\(value \= "/\{seckillId\}/exposer"\,
 method \= RequestMethod\.POST\,
 produces \= \{"application/json\;charset\=UTF\-8"\}\)
 \@ResponseBody
 public SeckillResult\<Exposer\> exposer\(Long seckillId\)\{
 SeckillResult\<Exposer\> result\;
 try \{
 Exposer exposer \= seckillService\.exportSeckillUrl\(seckillId\)\;
 result \= new SeckillResult\<Exposer\>\(true\, exposer\)\;
 \} catch \(Exception e\) \{
 logger\.error\(e\.getMessage\(\)\, e\)\;
 result \= new SeckillResult\<Exposer\>\(false\, e\.getMessage\(\)\)\;
 \}
 return result\;
 \}

 \@RequestMapping\(value \= "/\{seckillId\}/\{md5\}/execution"\,
 method \= RequestMethod\.POST\,
 produces \= \{"application/json\;charset\=UTF\-8"\}\)
 \@ResponseBody
 public SeckillResult\<SeckillExecution\> execute\(\@PathVariable\("seckillId"\) Long seckillId\,
 \@PathVariable\("md5"\)String md5\,
 \@CookieValue\(value \= "killPhone"\, required \= false\) Long phone\)\{
 //springmvc valid
 if \(phone \=\= null\) \{
 return new SeckillResult\<SeckillExecution\>\(false\, "未注册"\)\;
 \}

 SeckillResult\<SeckillExecution\> result\;
 try \{
 SeckillExecution execution \= seckillService\.executeSeckill\(seckillId\, phone\, md5\)\;
 return new SeckillResult\<SeckillExecution\>\(true\, execution\)\;
 \} catch\(RepeatKillException e\) \{
 SeckillExecution execution \= new SeckillExecution\(seckillId\, SeckillStateEnum\.REPEAT_KILL\)\;
 return new SeckillResult\<SeckillExecution\>\(false\,execution\)\;
 \} catch\(SeckillCloseException e\)\{
 SeckillExecution execution \= new SeckillExecution\(seckillId\, SeckillStateEnum\.END\)\;
 return new SeckillResult\<SeckillExecution\>\(false\,execution\)\;
 \} catch \(Exception e\)\{
 logger\.error\(e\.getMessage\(\)\,e\)\;
 SeckillExecution execution \= new SeckillExecution\(seckillId\, SeckillStateEnum\.INNER_ERROR\)\;
 return new SeckillResult\<SeckillExecution\>\(false\,execution\)\;
 \}
 return result\;
 \}
\}
